command:
/**
 * Generic resource adjustment.  Expects the following arguments:
 * uuid:    The UUID of the actor
 * amount:  Amount of damage to apply
 * type:    Damage type, will default to physical if unspecified.
 */

(async () => {
  try {
    console.log(args);
    const actor = await fromUuid(args[0]);
    if (!actor) throw new Error(`Actor ${args[0]} not found.`);

    const amt = parseFloat(args[1]);
    if (isNaN(amt)) throw new Error(`Invalid damage amount: ${args[1]}`)
    const dmgType = args[2] ?? "physical";

    console.log(actor, amt, dmgType);

    const DAMAGE_TYPES = ["physical", "air", "bolt", "dark", "earth", "fire", "ice", "light", "poison", "untyped"];

    if (!DAMAGE_TYPES.includes(dmgType)) throw new Error(`Unknown damage type: ${dmgType}`);

    const affinity = actor.system?.affinities[dmgType]?.current || 0;
    console.log(affinity);

    let dmg = 0;

    switch (affinity) {
      case 0:
        // None
        dmg = amt;
        break;
      case -1:
        // Vuln
        dmg = amt * 2;
        break;
      case 1:
        // Res
        dmg = Math.floor(amt / 2);
        break;
      case 2:
        // Im
        dmg = 0;
        ui.notifications?.info(`${actor.name} is immune to ${dmgType} damage.`, { console: false });
        break;
      case 3:
        // Absorb
        dmg = amt * -1;
        break;
      default:
        throw new Error(`Unknown affinity type: ${affinity}`);
    }
    console.log(dmg);

    await actor.update({
      system: {
        resources: {
          hp: {
            value: Math.min(Math.max(actor.system.resources.hp.value - dmg, 0), actor.system.resources.hp.max)
          }
        }
      }
    });
  } catch (err) {
    ui.notifications?.error(err.message);
  }
})();
